(function() {
  var STDOUT, StringStreamer, T, Tokenizer;

  STDOUT = function(atom) {
    return console.log(atom);
  };

  StringStreamer = function(output) {
    return function(string) {
      return string.split('').map(output);
    };
  };

  T = function(output) {
    return function(atom) {
      console.log(atom);
      return output(atom);
    };
  };

  Tokenizer = function(output) {
    var word;
    word = "";
    return function(character) {
      if (character.match(/\s/)) {
        if (word) {
          output({
            type: "word",
            value: word
          });
          return word = "";
        }
      } else {
        return word += character;
      }
    };
  };

  Object.extend(exports, {
    StringStreamer: StringStreamer,
    Tokenizer: Tokenizer,
    T: T
  });

}).call(this);

