(function() {
  var Stream;

  Stream = require('/main');

  describe("Stream", function() {
    it("should be like a list", function() {
      var stream;
      stream = Stream(1);
      return assert.equal(1, stream.first());
    });
    it("should be able to have a couple items", function() {
      var stream;
      stream = Stream(0, function() {
        return Stream(1);
      });
      assert.equal(0, stream.get(0), "First item is 0");
      return assert.equal(1, stream.get(1), "Second item is 1");
    });
    it("should be able to map", function() {
      var stream;
      stream = Stream(0, function() {
        return Stream(1, function() {
          return Stream(2);
        });
      }).map(function(i) {
        return i * i;
      });
      return assert.equal(4, stream.get(2));
    });
    return it("should be enumerable by each", function() {
      var called, stream;
      called = 0;
      stream = Stream(0, function() {
        return Stream(1, function() {
          return Stream(2);
        });
      }).each(function() {
        return called += 1;
      });
      return assert.equal(3, called);
    });
  });

}).call(this);
